import React, { useEffect, useState } from 'react'
import './Section.css'
import { gender, Categories } from "../config/productConfig"
import { CartState } from '../Context'

export default function Section() {
    const [selectedGender, setSelectedGender] = useState('')
    const getValues = (filterValue) =>  selectedGender === filterValue
    const handleRadioBtn = (e) => {
        setSelectedGender(e.target.value)   
    }
    console.log("ggg-> ", selectedGender)
    const { setProducts, products } = CartState()
    useEffect(() => {
        console.log("useeffect called")
        setProducts(products.filter((product) => product.gender !== selectedGender))
    }, [selectedGender])
    console.log("products in section---> ", products)
    return(
        <>
        <section className='container1'>
            
            <div >
                <div className='gender bold'>Gender:-</div>
                { gender.map((gen) => (
                    <div key={gen.value} className='radio'><input type="radio" name='gender' value={gen.value} checked={getValues(gen.value)} onChange={(e) => handleRadioBtn(e)}/>{gen.label}</div>
                ))}
            </div>
            <br />
            <div>
                <div className='categories bold'>Categories:-</div>
                { Categories.map((cat) => (
                    <div key={cat.value} className='check'><input type="checkbox" />{cat.label}</div>
                ))}
            </div>
        </section>
        </>
    )
}
